@Composable
private fun AnimatedEmoji() {
    // Placeholder animated emoji - can be replaced with Lottie when added
    var scale by remember { mutableFloatStateOf(1f) }

    LaunchedEffect(Unit) {
        while (true) {
            scale = 1.2f
            delay(500)
            scale = 1f
            delay(500)
        }
    }

    Text(
        text = "âœ¨",
        style = MaterialTheme.typography.displayLarge,
        modifier = Modifier
            .padding(16.dp)
    )
}

