@Composable fun AnimatedBellIcon( modifier: Modifier = Modifier, color: Color = Color.Black, isAnimating: Boolean = false ) { val infiniteTransition = rememberInfiniteTransition(label = "bell_animation")
val rotation by infiniteTransition.animateFloat( initialValue = -10f, targetValue = 10f, animationSpec = infiniteRepeatable( animation = tween(300, easing = EaseInOutSine), repeatMode = RepeatMode.Reverse ), label = "bell_rotation" )
Canvas(modifier = modifier.size(24.dp)) { rotate(if (isAnimating) rotation else 0f) { drawBell(color) } } }
private fun DrawScope.drawBell(color: Color) { val width = size.width val height = size.height
// Bell body path drawPath( path = androidx.compose.ui.graphics.Path().apply { // Bell outline moveTo(width * 0.5f, height * 0.1f) cubicTo( width * 0.3f, height * 0.1f, width * 0.15f, height * 0.3f, width * 0.15f, height * 0.55f ) lineTo(width * 0.15f, height * 0.7f) lineTo(width * 0.1f, height * 0.8f) lineTo(width * 0.9f, height * 0.8f) lineTo(width * 0.85f, height * 0.7f) lineTo(width * 0.85f, height * 0.55f) cubicTo( width * 0.85f, height * 0.3f, width * 0.7f, height * 0.1f, width * 0.5f, height * 0.1f ) close()
// Bell clapper moveTo(width * 0.5f, height * 0.85f) lineTo(width * 0.45f, height * 0.95f) lineTo(width * 0.55f, height * 0.95f) close() }, color = color ) }

