package com.example.kindspark.data

import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase
import androidx.room.TypeConverters
import android.content.Context

@Database(
    entities = [KindnessPrompt::class, KindnessCompletion::class, SkippedPrompt::class],
    version = 2,
    exportSchema = false
)
abstract class KindnessDatabase : RoomDatabase() {

    abstract fun kindnessDao(): KindnessPromptDao

    companion object {
        @Volatile
        private var INSTANCE: KindnessDatabase? = null

        fun getDatabase(context: Context): KindnessDatabase {
            return INSTANCE ?: synchronized(this) {
                val instance = Room.databaseBuilder(
                    context.applicationContext,
                    KindnessDatabase::class.java,
                    "kindness_database"
                ).build()
                INSTANCE = instance
                instance
            }
        }
    }
}

package com.example.kindspark.data

import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase
import androidx.room.TypeConverters
import android.content.Context

@Database(
    entities = [KindnessPrompt::class, KindnessCompletion::class, SkippedPrompt::class, UserProgress::class, DailyPromptSelection::class],
    version = 4,
    exportSchema = false
)
abstract class KindnessDatabase : RoomDatabase() {

    abstract fun kindnessDao(): KindnessPromptDao

    companion object {
        @Volatile
        private var INSTANCE: KindnessDatabase? = null

        fun getDatabase(context: Context): KindnessDatabase {
            return INSTANCE ?: synchronized(this) {
                val instance = Room.databaseBuilder(
                    context.applicationContext,
                    KindnessDatabase::class.java,
                    "kindness_database"
                ).build()
                INSTANCE = instance
                instance
            }
        }
    }
}

package com.example.kindspark.data

import androidx.room.Entity
import androidx.room.PrimaryKey
import java.time.LocalDate

@Entity(tableName = "kindness_prompts")
data class KindnessPrompt(
    @PrimaryKey val id: Int,
    val text: String,
    val category: String = "general"
)

@Entity(tableName = "kindness_completions")
data class KindnessCompletion(
    @PrimaryKey(autoGenerate = true)
    val id: Long = 0,
    val promptId: Int,
    val completedDate: String, // Using String to store LocalDate as YYYY-MM-DD
    val isFavorite: Boolean = false,
    val notes: String = ""
)

package com.example.kindspark.data

import androidx.room.Entity
import androidx.room.PrimaryKey
import java.time.LocalDate

@Entity(tableName = "kindness_prompts")
data class KindnessPrompt(
    @PrimaryKey val id: Int,
    val text: String,
    val category: String = "general"
)

@Entity(tableName = "kindness_completions")
data class KindnessCompletion(
    @PrimaryKey(autoGenerate = true)
    val id: Long = 0,
    val promptId: Int,
    val completedDate: String, // Using String to store LocalDate as YYYY-MM-DD
    val isFavorite: Boolean = false,
    val notes: String = ""
)

@Entity(tableName = "skipped_prompts")
data class SkippedPrompt(
    @PrimaryKey(autoGenerate = true)
    val id: Long = 0,
    val promptId: Int,
    val skippedDate: String, // Using String to store LocalDate as YYYY-MM-DD
    val reason: String = "" // Optional reason for skipping
)

@Entity(tableName = "daily_prompt_selection")
data class DailyPromptSelection(
    @PrimaryKey
    val date: String, // YYYY-MM-DD format
    val promptId: Int
)

data class KindnessPromptWithCompletion(
    val prompt: KindnessPrompt,
    val completion: KindnessCompletion?
)

package com.example.kindspark.data

import androidx.room.*
import kotlinx.coroutines.flow.Flow

@Dao
interface KindnessPromptDao {

    @Query("SELECT * FROM kindness_prompts")
    suspend fun getAllPrompts(): List<KindnessPrompt>

    @Query("SELECT * FROM kindness_prompts WHERE id = :id")
    suspend fun getPromptById(id: Int): KindnessPrompt?

    @Query("SELECT * FROM kindness_completions ORDER BY completedDate DESC")
    fun getAllCompletions(): Flow<List<KindnessCompletion>>

    @Query("SELECT * FROM kindness_completions WHERE completedDate = :date")
    suspend fun getCompletionByDate(date: String): KindnessCompletion?

    @Query("SELECT * FROM kindness_completions WHERE isFavorite = 1 ORDER BY completedDate DESC")
    fun getFavoriteCompletions(): Flow<List<KindnessCompletion>>

    @Insert(onConflict = OnConflictStrategy.IGNORE)
    suspend fun insertPrompts(prompts: List<KindnessPrompt>)

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertCompletion(completion: KindnessCompletion): Long

    @Update
    suspend fun updateCompletion(completion: KindnessCompletion)

    @Delete
    suspend fun deleteCompletion(completion: KindnessCompletion)

    @Query("SELECT COUNT(*) FROM kindness_completions WHERE completedDate BETWEEN :startDate AND :endDate")
    suspend fun getCompletionCountBetweenDates(startDate: String, endDate: String): Int

    @Query("SELECT COUNT(*) FROM kindness_prompts")
    suspend fun getPromptCount(): Int

    // Skipped prompts methods
    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertSkippedPrompt(skippedPrompt: SkippedPrompt): Long

    @Query("SELECT * FROM skipped_prompts WHERE skippedDate = :date")
    suspend fun getSkippedPromptsByDate(date: String): List<SkippedPrompt>

    @Query("SELECT promptId FROM skipped_prompts WHERE skippedDate = :date")
    suspend fun getSkippedPromptIdsByDate(date: String): List<Int>

    @Query("DELETE FROM skipped_prompts WHERE skippedDate < :cutoffDate")
    suspend fun deleteOldSkippedPrompts(cutoffDate: String)
}

package com.example.kindspark.data

import androidx.room.*
import kotlinx.coroutines.flow.Flow

@Dao
interface KindnessPromptDao {

    @Query("SELECT * FROM kindness_prompts")
    suspend fun getAllPrompts(): List<KindnessPrompt>

    @Query("SELECT * FROM kindness_prompts WHERE id = :id")
    suspend fun getPromptById(id: Int): KindnessPrompt?

    @Query("SELECT * FROM kindness_completions ORDER BY completedDate DESC")
    fun getAllCompletions(): Flow<List<KindnessCompletion>>

    @Query("SELECT * FROM kindness_completions WHERE completedDate = :date")
    suspend fun getCompletionByDate(date: String): KindnessCompletion?

    @Query("SELECT * FROM kindness_completions WHERE isFavorite = 1 ORDER BY completedDate DESC")
    fun getFavoriteCompletions(): Flow<List<KindnessCompletion>>

    @Insert(onConflict = OnConflictStrategy.IGNORE)
    suspend fun insertPrompts(prompts: List<KindnessPrompt>)

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertCompletion(completion: KindnessCompletion): Long

    @Update
    suspend fun updateCompletion(completion: KindnessCompletion)

    @Delete
    suspend fun deleteCompletion(completion: KindnessCompletion)

    @Query("SELECT COUNT(*) FROM kindness_completions WHERE completedDate BETWEEN :startDate AND :endDate")
    suspend fun getCompletionCountBetweenDates(startDate: String, endDate: String): Int

    @Query("SELECT COUNT(*) FROM kindness_prompts")
    suspend fun getPromptCount(): Int

    // Skipped prompts methods
    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertSkippedPrompt(skippedPrompt: SkippedPrompt): Long

    @Query("SELECT * FROM skipped_prompts WHERE skippedDate = :date")
    suspend fun getSkippedPromptsByDate(date: String): List<SkippedPrompt>

    @Query("SELECT promptId FROM skipped_prompts WHERE skippedDate = :date")
    suspend fun getSkippedPromptIdsByDate(date: String): List<Int>

    @Query("DELETE FROM skipped_prompts WHERE skippedDate < :cutoffDate")
    suspend fun deleteOldSkippedPrompts(cutoffDate: String)

    // UserProgress methods
    @Query("SELECT * FROM user_progress WHERE id = 1")
    suspend fun getUserProgress(): UserProgress?

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertOrUpdateUserProgress(userProgress: UserProgress)

    @Query("UPDATE user_progress SET currentStreak = :streak, lastCompletedDate = :date WHERE id = 1")
    suspend fun updateStreak(streak: Int, date: String)

    @Query("UPDATE user_progress SET bestStreak = :bestStreak WHERE id = 1")
    suspend fun updateBestStreak(bestStreak: Int)

    // Daily prompt selection methods
    @Query("SELECT * FROM daily_prompt_selection WHERE date = :date")
    suspend fun getDailyPromptSelection(date: String): DailyPromptSelection?

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertDailyPromptSelection(selection: DailyPromptSelection)

    @Query("DELETE FROM daily_prompt_selection WHERE date < :cutoffDate")
    suspend fun deleteOldDailyPromptSelections(cutoffDate: String)
}

package com.example.kindspark.data

import kotlinx.coroutines.flow.Flow
import java.time.LocalDate
import java.time.format.DateTimeFormatter
import kotlin.random.Random

class KindnessRepository(private val dao: KindnessPromptDao) {

    private val dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd")

    // Pre-defined kindness prompts
    private val defaultPrompts = listOf(
        KindnessPrompt(1, "Compliment someone genuinely.", "social"),
        KindnessPrompt(2, "Call or text a family member to check on them.", "family"),
        KindnessPrompt(3, "Help a stranger today in any small way.", "community"),
        KindnessPrompt(4, "Leave a positive comment on social media.", "digital"),
        KindnessPrompt(5, "Pick up litter you see on the ground.", "environment"),
        KindnessPrompt(6, "Support a local business with kind words or a review.", "community"),
        KindnessPrompt(7, "Hold the door open for someone.", "courtesy"),
        KindnessPrompt(8, "Write a short thank-you note to someone.", "gratitude"),
        KindnessPrompt(9, "Donate clothes or items you no longer need.", "charity"),
        KindnessPrompt(10, "Smile at a stranger and say hello.", "social"),
        KindnessPrompt(11, "Let someone go ahead of you in line.", "courtesy"),
        KindnessPrompt(12, "Offer to help a coworker with their tasks.", "workplace"),
        KindnessPrompt(13, "Send an encouraging message to a friend.", "friendship"),
        KindnessPrompt(14, "Pay for someone's coffee or meal.", "generosity"),
        KindnessPrompt(15, "Volunteer for a local charity or cause.", "community"),
        KindnessPrompt(16, "Listen actively to someone who needs to talk.", "empathy"),
        KindnessPrompt(17, "Forgive someone who has wronged you.", "forgiveness"),
        KindnessPrompt(18, "Share your knowledge or skills with others.", "teaching"),
        KindnessPrompt(19, "Give a genuine compliment to a service worker.", "appreciation"),
        KindnessPrompt(20, "Plant a flower or tree in your community.", "environment")
    )

    suspend fun initializeDatabase() {
        val promptCount = dao.getPromptCount()
        if (promptCount == 0) {
            dao.insertPrompts(defaultPrompts)
        }
    }

    suspend fun getDailyPrompt(date: LocalDate = LocalDate.now()): KindnessPromptWithCompletion {
        val dateString = date.format(dateFormatter)

        // Check if there's already a completion for today
        val existingCompletion = dao.getCompletionByDate(dateString)

        if (existingCompletion != null) {
            // Return the prompt that was already completed today
            val prompt = dao.getPromptById(existingCompletion.promptId)
            return KindnessPromptWithCompletion(prompt!!, existingCompletion)
        }

        // Get all prompts and select one randomly
        val allPrompts = dao.getAllPrompts()
        if (allPrompts.isEmpty()) {
            initializeDatabase()
            return getDailyPrompt(date)
        }

        // Use date as seed for consistent daily prompt
        val seed = date.toEpochDay()
        val randomPrompt = allPrompts[Random(seed).nextInt(allPrompts.size)]

        return KindnessPromptWithCompletion(randomPrompt, null)
    }

    suspend fun markPromptAsCompleted(promptId: Int, date: LocalDate = LocalDate.now(), notes: String = ""): Long {
        val dateString = date.format(dateFormatter)
        val completion = KindnessCompletion(
            promptId = promptId,
            completedDate = dateString,
            notes = notes
        )
        return dao.insertCompletion(completion)
    }

    suspend fun toggleFavorite(completion: KindnessCompletion) {
        val updatedCompletion = completion.copy(isFavorite = !completion.isFavorite)
        dao.updateCompletion(updatedCompletion)
    }

        return dao.getFavoriteCompletions()
    }

    suspend fun getPromptById(id: Int): KindnessPrompt? {
        return dao.getPromptById(id)
    }

    suspend fun updateCompletion(completion: KindnessCompletion) {
        dao.updateCompletion(completion)
