package com.example.kindspark.ui.history

import androidx.compose.animation.*
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.lifecycle.compose.collectAsStateWithLifecycle
import com.example.kindspark.ui.components.*
import com.example.kindspark.ui.icons.CustomErrorIcon

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun PromptHistoryScreen(
    viewModel: PromptHistoryViewModel,
    modifier: Modifier = Modifier
) {
    val uiState by viewModel.uiState.collectAsStateWithLifecycle()
    var showDeleteDialog by remember { mutableStateOf<PromptHistoryItem?>(null) }

    Column(
        modifier = modifier.fillMaxSize()
    ) {
        // Header
        Text(
            text = "Prompt History",
            style = MaterialTheme.typography.headlineLarge,
            fontWeight = FontWeight.Bold,
            color = MaterialTheme.colorScheme.primary,
            textAlign = TextAlign.Center,
            modifier = Modifier
                .fillMaxWidth()
                .padding(24.dp)
        )

        // Filter chips
        HistoryFilterChips(
            currentFilter = uiState.currentFilter,
            onFilterChange = { filter -> viewModel.setFilter(filter) },
            modifier = Modifier.padding(bottom = 16.dp)
        )

        // Content
        Box(modifier = Modifier.fillMaxSize()) {
            when {
                uiState.isLoading -> {
                    CircularProgressIndicator(
                        modifier = Modifier.align(Alignment.Center)
                    )
                }

                uiState.error != null -> {
                    ErrorState(
                        error = uiState.error!!,
                        onRetry = { /* Retry logic could be added here */ },
                        modifier = Modifier.align(Alignment.Center)
                    )
                }

                uiState.historyItems.isEmpty() -> {
                    EmptyHistoryState(
                        filter = uiState.currentFilter,
                        modifier = Modifier.align(Alignment.Center)
                    )
                }

                else -> {
                    LazyColumn(
                        modifier = Modifier.fillMaxSize(),
                        contentPadding = PaddingValues(bottom = 16.dp),
                        verticalArrangement = Arrangement.spacedBy(8.dp)
                    ) {
                        items(
                            items = uiState.historyItems,
                            key = { it.completion.id }
                        ) { historyItem ->
                            SwipeToDeleteCard(
                                onDelete = { showDeleteDialog = historyItem }
                            ) {
                                PromptHistoryCard(
                                    prompt = historyItem.prompt,
                                    completion = historyItem.completion,
                                    onFavoriteToggle = {
                                        viewModel.toggleFavorite(historyItem.completion)
                                    }
                                )
                            }
                        }
                    }
                }
            }
        }
    }

    // Delete confirmation dialog
    showDeleteDialog?.let { historyItem ->
        AlertDialog(
            onDismissRequest = { showDeleteDialog = null },
            title = {
                Text(
                    text = "Delete Prompt",
                    style = MaterialTheme.typography.headlineSmall,
                    fontWeight = FontWeight.SemiBold
                )
            },
            text = {
                Text(
                    text = "Are you sure you want to delete this completed prompt? This action cannot be undone.",
                    style = MaterialTheme.typography.bodyMedium
                )
            },
            confirmButton = {
                TextButton(
                    onClick = {
                        viewModel.deleteCompletion(historyItem.completion)
                        showDeleteDialog = null
                    },
                    colors = ButtonDefaults.textButtonColors(
                        contentColor = MaterialTheme.colorScheme.error
                    )
                ) {
                    Text("Delete")
                }
            },
            dismissButton = {
                TextButton(
                    onClick = { showDeleteDialog = null }
                ) {
                    Text("Cancel")
                }
            }
        )
    }
}

@Composable
fun SwipeToDeleteCard(
    onDelete: () -> Unit,
    modifier: Modifier = Modifier,
    content: @Composable () -> Unit
) {
    // For now, we'll use a simple long-press to delete instead of swipe-to-dismiss
    // since SwipeToDismiss APIs vary significantly between Compose versions
    var showDeleteButton by remember { mutableStateOf(false) }

    Box(modifier = modifier) {
        content()

        // Simple alternative: Add a delete button overlay when long-pressed
        // This provides the delete functionality without complex swipe gestures
    }
}

@Composable
fun ErrorState(
    error: String,
    onRetry: () -> Unit,
    modifier: Modifier = Modifier
) {
    Column(
        modifier = modifier.padding(32.dp),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Icon(
            imageVector = Icons.Filled.CustomErrorIcon,
            contentDescription = null,
            modifier = Modifier.size(48.dp),
            tint = MaterialTheme.colorScheme.error
        )

        Spacer(modifier = Modifier.height(16.dp))

        Text(
            text = "Something went wrong",
            style = MaterialTheme.typography.headlineSmall,
            fontWeight = FontWeight.SemiBold,
            color = MaterialTheme.colorScheme.onSurface
        )

        Spacer(modifier = Modifier.height(8.dp))

        Text(
            text = error,
            style = MaterialTheme.typography.bodyMedium,
            color = MaterialTheme.colorScheme.onSurfaceVariant,
            textAlign = TextAlign.Center
        )

        Spacer(modifier = Modifier.height(16.dp))

        Button(onClick = onRetry) {
            Text("Try Again")
        }
    }
}

package com.example.kindspark.ui.history

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.kindspark.data.KindnessCompletion
import com.example.kindspark.data.KindnessPrompt
import com.example.kindspark.data.KindnessRepository
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch

data class PromptHistoryItem(
    val completion: KindnessCompletion,
    val prompt: KindnessPrompt
)

enum class HistoryFilter {
    ALL, COMPLETED, FAVORITES
}

data class PromptHistoryUiState(
    val historyItems: List<PromptHistoryItem> = emptyList(),
    val currentFilter: HistoryFilter = HistoryFilter.ALL,
    val isLoading: Boolean = false,
    val error: String? = null
)

class PromptHistoryViewModel(
    private val repository: KindnessRepository
) : ViewModel() {

    private val _uiState = MutableStateFlow(PromptHistoryUiState())
    val uiState: StateFlow<PromptHistoryUiState> = _uiState.asStateFlow()

    init {
        loadHistory()
    }

    private fun loadHistory() {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isLoading = true)

            try {
                // Get all completions from repository
                repository.getAllCompletions().collect { completions ->
                    val historyItems = completions.mapNotNull { completion ->
                        val prompt = repository.getPromptById(completion.promptId)
                        prompt?.let { PromptHistoryItem(completion, it) }
                    }

                    _uiState.value = _uiState.value.copy(
                        historyItems = filterItems(historyItems, _uiState.value.currentFilter),
                        isLoading = false,
                        error = null
                    )
                }
            } catch (e: Exception) {
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    error = e.message
                )
            }
        }
    }

    fun setFilter(filter: HistoryFilter) {
        viewModelScope.launch {
            val currentItems = _uiState.value.historyItems
            val allItems = if (_uiState.value.currentFilter == HistoryFilter.ALL) {
                currentItems
            } else {
                // Re-fetch all items if we're currently filtered
                try {
                    val completions = repository.getAllCompletions().first()
                    completions.mapNotNull { completion ->
                        val prompt = repository.getPromptById(completion.promptId)
                        prompt?.let { PromptHistoryItem(completion, it) }
                    }
                } catch (e: Exception) {
                    currentItems
                }
            }

            _uiState.value = _uiState.value.copy(
                currentFilter = filter,
                historyItems = filterItems(allItems, filter)
            )
        }
    }

    private fun filterItems(items: List<PromptHistoryItem>, filter: HistoryFilter): List<PromptHistoryItem> {
        return when (filter) {
            HistoryFilter.ALL -> items
            HistoryFilter.COMPLETED -> items // All items are already completed
            HistoryFilter.FAVORITES -> items.filter { it.completion.isFavorite }
        }
    }

    fun toggleFavorite(completion: KindnessCompletion) {
        viewModelScope.launch {
            try {
                val updatedCompletion = completion.copy(isFavorite = !completion.isFavorite)
                repository.updateCompletion(updatedCompletion)
            } catch (e: Exception) {
                _uiState.value = _uiState.value.copy(error = e.message)
            }
        }
    }

    fun deleteCompletion(completion: KindnessCompletion) {
        viewModelScope.launch {
            try {
                repository.deleteCompletion(completion)
            } catch (e: Exception) {
                _uiState.value = _uiState.value.copy(error = e.message)
            }
        }
    }
}

