suspend fun KindnessRepository.getPromptById(id: Int): KindnessPrompt? {
    // This would need to be added to the repository if not already present
    return try {
        // Assuming there's a method in DAO to get prompt by ID
        getAllPrompts().find { it.id == id }
    } catch (e: Exception) {
        null
    }
}

