@Composable fun AnimatedBellIcon( modifier: Modifier = Modifier, color: Color = Color.Black, isAnimating: Boolean = false ) { val infiniteTransition = rememberInfiniteTransition(label = "bell_animation")
val rotation by infiniteTransition.animateFloat( initialValue = -10f, targetValue = 10f, animationSpec = infiniteRepeatable( animation = tween(300, easing = EaseInOutSine), repeatMode = RepeatMode.Reverse ), label = "bell_rotation" )
Canvas(modifier = modifier.size(24.dp)) { rotate(if (isAnimating) rotation else 0f) { drawBell(color) } } }
