package com.example.kindspark.ui.settings

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyRow
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.rememberScrollState
import androidx.compose.foundation.verticalScroll
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.runtime.saveable.rememberSaveable
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.lifecycle.compose.collectAsStateWithLifecycle
import com.example.kindspark.data.preferences.UserPreferencesManager
import com.example.kindspark.ui.components.*
import com.example.kindspark.ui.icons.*

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SettingsScreen(
    viewModel: SettingsViewModel,
    modifier: Modifier = Modifier
) {
    val uiState by viewModel.uiState.collectAsStateWithLifecycle()
    var showIntervalDialog by remember { mutableStateOf(false) }
    var showResetDialog by remember { mutableStateOf(false) }

    // State for expandable sections
    var themeExpanded by rememberSaveable { mutableStateOf(true) }
    var notificationsExpanded by rememberSaveable { mutableStateOf(true) }
    var aboutExpanded by rememberSaveable { mutableStateOf(false) }

    Column(
        modifier = modifier
            .fillMaxSize()
            .verticalScroll(rememberScrollState())
    ) {
        // Modern Header
        Text(
            text = "Settings",
            style = MaterialTheme.typography.headlineLarge,
            fontWeight = FontWeight.Bold,
            color = MaterialTheme.colorScheme.primary,
            textAlign = TextAlign.Center,
            modifier = Modifier
                .fillMaxWidth()
                .padding(24.dp)
        )

        if (uiState.isLoading) {
            Box(
                modifier = Modifier.fillMaxSize(),
                contentAlignment = Alignment.Center
            ) {
                CircularProgressIndicator()
            }
        } else {
            // Theme Section
            ModernSettingSection(
                title = "Theme",
                isExpanded = themeExpanded,
                onToggleExpanded = { themeExpanded = !themeExpanded }
            ) {
                ModernSettingItem(
                    icon = Icons.Outlined.CustomPalette,
                    title = "App Theme",
                    subtitle = "Choose your preferred visual style"
                )

                // Dynamic Theme Previews
                LazyRow(
                    modifier = Modifier.padding(horizontal = 20.dp),
                    horizontalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    items(UserPreferencesManager.AppTheme.values()) { theme ->
                        ThemePreviewCard(
                            theme = theme,
                            isSelected = theme == uiState.selectedTheme,
                            onClick = { viewModel.updateSelectedTheme(theme) }
                        )
                    }
                }

                Spacer(modifier = Modifier.height(16.dp))

                ModernSettingItem(
                    icon = Icons.Default.Settings,
                    title = "Dark Mode",
                    subtitle = "Enable dark theme across the app",
                    trailing = {
                        ModernSwitch(
                            checked = uiState.darkMode,
                            onCheckedChange = { viewModel.updateDarkMode(it) }
                        )
                    }
                )

                ModernSettingItem(
                    icon = Icons.Outlined.CustomLandscape,
                    title = "Calming Backgrounds",
                    subtitle = "Use gentle gradient backgrounds",
                    trailing = {
                        ModernSwitch(
                            checked = uiState.calmingBackground,
                            onCheckedChange = { viewModel.updateCalmingBackground(it) }
                        )
                    }
                )

                ModernSettingItem(
                    icon = Icons.Outlined.CustomAutoAwesome,
                    title = "Celebration Animations",
                    subtitle = "Show animations when completing tasks",
                    trailing = {
                        ModernSwitch(
                            checked = uiState.lottieAnimations,
                            onCheckedChange = { viewModel.updateLottieAnimations(it) }
                        )
                    }
                )
            }

            // Notifications Section
            ModernSettingSection(
                title = "Notifications",
                isExpanded = notificationsExpanded,
                onToggleExpanded = { notificationsExpanded = !notificationsExpanded }
            ) {
                ModernSettingItem(
                    icon = Icons.Outlined.AnimatedBell,
                    title = "Daily Reminders",
                    subtitle = "Get reminded to perform daily acts of kindness",
                    trailing = {
                        ModernSwitch(
                            checked = uiState.notificationEnabled,
                            onCheckedChange = { viewModel.updateNotificationEnabled(it) }
                        )
                    }
                )

                if (uiState.notificationEnabled) {
                    ModernSettingItem(
                        icon = Icons.Outlined.CustomSchedule,
                        title = "Notification Frequency",
                        subtitle = uiState.notificationInterval.displayName,
                        onClick = { showIntervalDialog = true }
                    )

                    ModernSettingItem(
                        icon = Icons.Outlined.CustomVolumeUp,
                        title = "Notification Sound",
                        subtitle = "Play sound with notifications",
                        trailing = {
                            ModernSwitch(
                                checked = uiState.notificationSound,
                                onCheckedChange = { viewModel.updateNotificationSound(it) }
                            )
                        }
                    )
                }
            }

            // About Section
            ModernSettingSection(
                title = "About",
                isExpanded = aboutExpanded,
                onToggleExpanded = { aboutExpanded = !aboutExpanded }
            ) {
                ModernSettingItem(
                    icon = Icons.Default.Info,
                    title = "Version",
                    subtitle = "2.0.0 - Enhanced with Modern UX"
                )

                ModernSettingItem(
                    icon = Icons.Outlined.SparklingHeart,
                    title = "Made with ❤️",
                    subtitle = "Crafted to spread kindness and positivity"
                )

                ModernSettingItem(
                    icon = Icons.Outlined.AchievementStar,
                    title = "Rate the App",
                    subtitle = "Help us improve by sharing your feedback"
                )
            }

            // Reset Button at Bottom
            ResetSettingsButton(
                onClick = { showResetDialog = true }
            )

            // Add bottom padding for better scrolling experience
            Spacer(modifier = Modifier.height(24.dp))
        }
    }

    // Notification Interval Dialog
    if (showIntervalDialog) {
        AlertDialog(
            onDismissRequest = { showIntervalDialog = false },
            title = {
                Text(
                    text = "Notification Frequency",
                    style = MaterialTheme.typography.headlineSmall,
                    fontWeight = FontWeight.SemiBold
                )
            },
            text = {
                Column {
                    Text(
                        text = "How often would you like to receive reminders?",
                        style = MaterialTheme.typography.bodyMedium,
                        color = MaterialTheme.colorScheme.onSurfaceVariant
                    )
                    Spacer(modifier = Modifier.height(16.dp))

                    UserPreferencesManager.NotificationInterval.values().forEach { interval ->
                        Row(
                            modifier = Modifier
                                .fillMaxWidth()
                                .padding(vertical = 8.dp),
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            RadioButton(
                                selected = interval == uiState.notificationInterval,
                                onClick = {
                                    viewModel.updateNotificationInterval(interval)
                                    showIntervalDialog = false
                                }
                            )
                            Spacer(modifier = Modifier.width(12.dp))
                            Text(
                                text = interval.displayName,
                                style = MaterialTheme.typography.bodyLarge
                            )
                        }
                    }
                }
            },
            confirmButton = {
                TextButton(
                    onClick = { showIntervalDialog = false }
                ) {
                    Text("Done")
                }
            }
        )
    }

    // Reset Settings Dialog
    if (showResetDialog) {
        AlertDialog(
            onDismissRequest = { showResetDialog = false },
            title = {
                Text(
                    text = "Reset Settings",
                    style = MaterialTheme.typography.headlineSmall,
                    fontWeight = FontWeight.SemiBold
                )
            },
            text = {
                Text(
                    text = "Are you sure you want to reset all settings to their default values? This action cannot be undone.",
                    style = MaterialTheme.typography.bodyMedium
                )
            },
            confirmButton = {
                TextButton(
                    onClick = {
                        viewModel.resetToDefaults()
                        showResetDialog = false
                    },
                    colors = ButtonDefaults.textButtonColors(
                        contentColor = MaterialTheme.colorScheme.error
                    )
                ) {
                    Text("Reset")
                }
            },
            dismissButton = {
                TextButton(
                    onClick = { showResetDialog = false }
                ) {
                    Text("Cancel")
                }
            }
        )
    }
}

package com.example.kindspark.ui.settings

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.kindspark.data.preferences.UserPreferencesManager
import com.example.kindspark.notifications.NotificationScheduler
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch

data class SettingsUiState(
    val notificationEnabled: Boolean = true,
    val notificationInterval: UserPreferencesManager.NotificationInterval = UserPreferencesManager.NotificationInterval.ONCE_DAILY,
    val notificationSound: Boolean = true,
    val selectedTheme: UserPreferencesManager.AppTheme = UserPreferencesManager.AppTheme.LIGHT,
    val calmingBackground: Boolean = true,
    val lottieAnimations: Boolean = true,
    val isLoading: Boolean = false
)

class SettingsViewModel(
    private val preferencesManager: UserPreferencesManager,
    private val notificationScheduler: NotificationScheduler
) : ViewModel() {

    private val _uiState = MutableStateFlow(SettingsUiState())
    val uiState: StateFlow<SettingsUiState> = _uiState.asStateFlow()

    init {
        loadSettings()
    }

    private fun loadSettings() {
        viewModelScope.launch {
            combine(
                preferencesManager.notificationEnabled,
                preferencesManager.notificationInterval,
                preferencesManager.notificationSound,
                preferencesManager.selectedTheme,
                preferencesManager.calmingBackground,
                preferencesManager.lottieAnimations
            ) { values ->
                val notificationEnabled = values[0] as Boolean
                val notificationInterval = values[1] as UserPreferencesManager.NotificationInterval
                val notificationSound = values[2] as Boolean
                val selectedTheme = values[3] as UserPreferencesManager.AppTheme
                val calmingBackground = values[4] as Boolean
                val lottieAnimations = values[5] as Boolean

                SettingsUiState(
                    notificationEnabled = notificationEnabled,
                    notificationInterval = notificationInterval,
                    notificationSound = notificationSound,
                    selectedTheme = selectedTheme,
                    calmingBackground = calmingBackground,
                    lottieAnimations = lottieAnimations,
                    isLoading = false
                )
            }.collect { newState ->
                _uiState.value = newState
            }
        }
    }

    fun updateNotificationEnabled(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateNotificationEnabled(enabled)
            // Reschedule or cancel notifications based on the new setting
            if (enabled) {
                notificationScheduler.scheduleNotifications()
            } else {
                notificationScheduler.cancelNotifications()
            }
        }
    }

    fun updateNotificationInterval(interval: UserPreferencesManager.NotificationInterval) {
        viewModelScope.launch {
            preferencesManager.updateNotificationInterval(interval)
            // Reschedule notifications with the new interval
            if (_uiState.value.notificationEnabled) {
                notificationScheduler.rescheduleNotifications()
            }
        }
    }

    fun updateNotificationSound(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateNotificationSound(enabled)
        }
    }

    fun updateSelectedTheme(theme: UserPreferencesManager.AppTheme) {
        viewModelScope.launch {
            preferencesManager.updateSelectedTheme(theme)
        }
    }

    fun updateCalmingBackground(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateCalmingBackground(enabled)
        }
    }

    fun updateLottieAnimations(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateLottieAnimations(enabled)
        }
    }
}

package com.example.kindspark.ui.settings

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.kindspark.data.preferences.UserPreferencesManager
import com.example.kindspark.notifications.NotificationScheduler
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch

data class SettingsUiState(
    val notificationEnabled: Boolean = true,
    val notificationInterval: UserPreferencesManager.NotificationInterval = UserPreferencesManager.NotificationInterval.ONCE_DAILY,
    val notificationSound: Boolean = true,
    val selectedTheme: UserPreferencesManager.AppTheme = UserPreferencesManager.AppTheme.LIGHT,
    val darkMode: Boolean = false,
    val calmingBackground: Boolean = true,
    val lottieAnimations: Boolean = true,
    val isLoading: Boolean = false
)

class SettingsViewModel(
    private val preferencesManager: UserPreferencesManager,
    private val notificationScheduler: NotificationScheduler
) : ViewModel() {

    private val _uiState = MutableStateFlow(SettingsUiState())
    val uiState: StateFlow<SettingsUiState> = _uiState.asStateFlow()

    init {
        loadSettings()
    }

    private fun loadSettings() {
        viewModelScope.launch {
            combine(
                preferencesManager.notificationEnabled,
                preferencesManager.notificationInterval,
                preferencesManager.notificationSound,
                preferencesManager.selectedTheme,
                preferencesManager.darkMode,
                preferencesManager.calmingBackground,
                preferencesManager.lottieAnimations
            ) { values ->
                val notificationEnabled = values[0] as Boolean
                val notificationInterval = values[1] as UserPreferencesManager.NotificationInterval
                val notificationSound = values[2] as Boolean
                val selectedTheme = values[3] as UserPreferencesManager.AppTheme
                val darkMode = values[4] as Boolean
                val calmingBackground = values[5] as Boolean
                val lottieAnimations = values[6] as Boolean

                SettingsUiState(
                    notificationEnabled = notificationEnabled,
                    notificationInterval = notificationInterval,
                    notificationSound = notificationSound,
                    selectedTheme = selectedTheme,
                    darkMode = darkMode,
                    calmingBackground = calmingBackground,
                    lottieAnimations = lottieAnimations,
                    isLoading = false
                )
            }.collect { newState ->
                _uiState.value = newState
            }
        }
    }

    fun updateNotificationEnabled(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateNotificationEnabled(enabled)
            // Reschedule or cancel notifications based on the new setting
            if (enabled) {
                notificationScheduler.scheduleNotifications()
            } else {
                notificationScheduler.cancelNotifications()
            }
        }
    }

    fun updateNotificationInterval(interval: UserPreferencesManager.NotificationInterval) {
        viewModelScope.launch {
            preferencesManager.updateNotificationInterval(interval)
            // Reschedule notifications with the new interval
            if (_uiState.value.notificationEnabled) {
                notificationScheduler.rescheduleNotifications()
            }
        }
    }

    fun updateNotificationSound(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateNotificationSound(enabled)
        }
    }

    fun updateSelectedTheme(theme: UserPreferencesManager.AppTheme) {
        viewModelScope.launch {
            preferencesManager.updateSelectedTheme(theme)
        }
    }

    fun updateDarkMode(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateDarkMode(enabled)
        }
    }

    fun updateCalmingBackground(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateCalmingBackground(enabled)
        }
    }

    fun updateLottieAnimations(enabled: Boolean) {
        viewModelScope.launch {
            preferencesManager.updateLottieAnimations(enabled)
        }
    }

    fun resetToDefaults() {
        viewModelScope.launch {
            preferencesManager.resetToDefaults()
            // Reschedule notifications with default settings
            notificationScheduler.scheduleNotifications()
        }
    }
}

